INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('A. K.', 'Roseanne Park (born 11 February 1997), known mononymously as Rosé (Korean: 로제), is a New Zealand and South Korean singer and songwriter. Born in Auckland, New Zealand, and raised in Melbourne, Australia, Rosé moved to Seoul, South Korea and signed with label YG Entertainment following a successful audition in Sydney in 2012 and trained for four years before debuting as a member of the South Korean girl group Blackpink in August 2016.
