INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Aasim Ali', 'Abrar-ul-Haq (Punjabi: ابرار الحق) is a Pakistani singer-songwriter, philanthropist, and a politician. 
