INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Anir', 'Harry Belafonte ( BEL-ə-FON-tee; born Harold George Bellanfanti Jr.; March 1, 1927 – April 25, 2023) was an American singer, actor, and civil rights activist who popularized calypso music with international audiences in the 1950s and 1960s. Belafonte''s career breakthrough album Calypso (1956) was the first million-selling LP by a single artist.
