INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Anuv Jain', 'Anuv Jain is an Indian singer songwriter and composer. He is known for his simple chorded songs with lyrics that deal with love and heartbreak. His music primarily contains an acoustic guitar or ukulele, unaccompanied by percussion or other instruments.
