INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Azam', 'Mahbubul Haque Khan (28 February 1950 – 5 June 2011), known as Azam Khan, was a Bangladeshi singer-songwriter, record producer, and lead singer for the pop-rock band Uccharon. He was also a freedom fighter who took part in the Liberation War of Bangladesh in 1971. He is considered to be one of the greatest artists in the history of Bangladeshi folk music and a founding figure of Bangla rock.
