INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Badnaam Badshah', 'Aditya Prateek Singh better known by his stage name Badshah, is an Indian rapper, singer, songwriter, music producer, television personality and businessman. Badshah has recorded his songs in Hindi, Punjabi, English and Haryanvi languages.  
