INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Chaahat', 'Jonita Gandhi (born October 23, 1989), credited professionally as Jonita, is a Canadian singer known for her work in the Indian film and music industries. Since 2024, she has performed as a solo pop artist by releasing her debut Independent EP single Love Like That.
