INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Cine Singers Association Chorus Group', 'Michael Holbrook Penniman Jr. (born 18 August 1983), known professionally as Mika ( MEE-kə, stylised in all caps), is a singer-songwriter born in Beirut, Lebanon, and raised in Paris and London.
