INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Colin Terence', 'Colin Charles Greenwood (born 26 June 1969) is an English bassist and a member of the rock band Radiohead. Along with bass guitar, Greenwood plays upright bass and electronic instruments.
