INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('DJ Bravo', 'Clifford Joseph Price MBE (born 19 September 1965), better known as Goldie, is an English music producer, DJ, graffiti artist and actor.
