INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Daljeet Chahal', 'Punjabi cinema, also known as Pollywood or Punjwood, is cinema dedicated to the production of motion pictures in the Punjabi-language widely spoken in the Indian state of Punjab. It is based in Amritsar, Ludhiana, and Mohali.
