INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Gopi Sidhu', 'Jasbir Singh Bains (born 7 February 1970), better known by his stage name Jasbir Jassi, is an Indian singer, lyricist, performer and actor. As of 2016 he has released thirteen albums, his first pop album being Dil Le Gayee, released in 1998 with Times Music.', '1970-02-07', 0);

INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Gopi Sunder', 'Gopi Sundar C. S. (born 30 May 1977) is an Indian music director, programmer, playback singer, songwriter, actor and performer who works in the Malayalam, Telugu and Tamil film industries.
