INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Hashmat', 'Krishnan Nair Shantakumari Chithra (born 27 July 1963) is an Indian playback singer and Carnatic musician. In a career spanning around five decades, she has recorded over 25,000 Songs  in various Indian languages languages such as Tamil, Kannada, Hindi, Telugu, Malayalam, Odia, Bengali, Punjabi, Rajasthani, Awadhi, Marathi, Tulu, Badaga,  Braj, Banjara, Urdu, Assamese, Gujarati, Manipuri and Sanskrit, as well as foreign languages such as Malay, Latin, Arabic, Sinhalese, English and French.
