INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Herbie Sahara', 'Isaac Lee Hayes Jr. (August 20, 1942 – August 10, 2008) was an American singer, songwriter, composer, and actor. He was one of the creative forces behind the Southern soul music label Stax Records in the 1960s, serving as an in-house songwriter with his partner David Porter, 
