INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Imanbek', 'Alan Olav Walker (born 24 August 1997) is a Norwegian  DJ and record producer. His songs "Faded", "Sing Me to Sleep", "Alone", "All Falls Down", "Ignite" and "Darkside" have each been multi-platinum-certified and reached number 1 on the VG-lista chart in Norway. Walker values his anonymity and is known to wear a hoodie and mask to remain inconspicuous.
