INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Jisha', 'Shaji Neelakantan Karunakaran (1 January 1952 – 28 April 2025), better known as Shaji N. Karun, was 
