INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Jolly Kang', 'Anne, alternatively spelled Ann, is a form of the Latin female name Anna. This in turn is a representation of the Hebrew Hannah, which means ''favour'' or ''grace''. Related names include Annie and Ana.
