INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Jyoti', 'Nooran Sisters are Sultana Nooran (born 14 June 1992) and Jyoti Nooran (born 24 February 1994), an Indian devotional Sufi singing duo. Born to a family of Sufi musicians, they perform Sham Chaurasia gharana classical music. They gained public recognition in 2012, when their song "Tung Tung" was featured on MTV Sound Trippin of MTV India and was used in the soundtrack for the 2015 film Singh is Bling.
