INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Love Dutta', 'Rajeshwari Dutta  born as Rajeshwari Vasudev (17 July 1918 - 10 April 1976) was an Indian singer, considered one of the best-known exponents of the songs of Rabindranath Tagore. 
