INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Luv O Trigger', 'Park Jae-sang (Korean: 박재상; pronounced [pɐk̚.t͡ɕεsɐŋ]; born December 31, 1977), better known by his stage name Psy ( SY; 싸이), is a South Korean rapper and singer-songwriter. He has received a selection of accolades, including an American Music Award, ten MAMA Awards, and a World Music Award.
