INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Mahfuz Ali', 'Faruq Mahfuz Anam James (born 2 October 1965), known mononymously as James, is a Bangladeshi singer-songwriter, guitarist and composer, also known as a playback singer. He is the lead singer, songwriter and guitarist of the rock band Nagar Baul (previously Feelings).
