INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Mahi Gaur', 'Ashok Manda Bishnoi, better known by his stage name Rapperiya Baalam, is a rapper, songwriter, music composer, and producer based in Jaipur, Rajasthan. He rose to fame in 2014 with his song "Mharo Rajasthan", a folk–hip hop fusion song featuring Swaroop Khan. 
