INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Marshmello', 'Christopher Comstock (born May 19, 1992), known professionally as Marshmello, is an American DJ and record producer. His songs "Silence" (featuring Khalid), "Wolves" (with Selena Gomez), "Friends" (with Anne-Marie), "Happier" (with Bastille), and "Alone" have each received multi-platinum certifications in several countries, and peaked within the top 40 of the Billboard Hot 100. His musical style includes groove-oriented, synth and bass-heavy electronic dance music.
