INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Maurice La Marche', 'Maurice Auguste Chevalier (French: [mɔʁis ʃəvalje]; 12 September 1888 – 1 January 1972) was a French singer, actor, and entertainer. He is best known for his signature songs, including "Livin'' In The Sunlight", "Valentine", "Louise", "Mimi", and "Thank Heaven for Little Girls", and for his films, including The Love Parade, The Big Pond, The Smiling Lieutenant, One Hour with You, and Love Me Tonight. His trademark attire was a boater hat and tuxedo.
