INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Meer', 'Mathangi Arulpragasam (Tamil: மாதங்கி அருள்பிரகாசம்; born 18 July 1975), known as Maya and professionally as M.I.A. (Tamil: எம்.ஐ.ஏ.; an initialism for both "Missing in action" and "Missing in Acton"), is a British singer, rapper, songwriter, record producer, and activist. Her music combines elements of alternative, dance, electronic, hip hop and world music with electronic instruments and samples.
