INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Mr. Sohu', 'Jessica Jung (born April 18, 1989) is an American and South Korean singer-songwriter, actress, author, and businesswoman. She is best known for her work as a former member of the South Korean girl group Girls'' Generation. As an actress, Jung portrayed Elle Woods in the Korean version of the musical Legally Blonde in 2010 and played a role in the television drama Wild Romance in 2012.
