INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Muskan Yadav', 'Kalyanji–Anandji were an Indian composer duo: Kalyanji Virji Shah (30 June 1928 – 24 August 2000) and his brother Anandji Virji Shah (born 2 March 1933). The duo are known for their work on Hindi film soundtracks, with many evergreen songs being composed by them.
