INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Muzamil Bhawani', 'Abu''l Hasan Yamīn ud-Dīn Khusrau (1253 – October 1325), better known as Amīr Khusrau, sometimes spelled as, Amir Khusrow or Amir Khusro, was an Indo-Persian Sufi singer, musician, poet and scholar, who lived during the period of the Delhi Sultanate.

