INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('NISH MUSIC', 'Muhammad Mumith Ahmed (Bengali: মুহাম্মদ মুমিথ আহমেদ; born 18 January 1984), known professionally as Mumzy Stranger or his producing name SP (an abbreviation of Stranger Productions), is a British rapper, singer, songwriter and record producer. He made his mainstream recording debut in the summer of 2009 with his debut single "One More Dance" along with follow-up single, "Showgirl". The following year, he released Fly with Me which is the lead single from his debut album, Journey Begins.
