INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Neeraj Shridhar', 'Bombay Vikings are a pop group that combine Indian pop and classical music, formed in 1994 in Stockholm, Sweden. The band was started by Neeraj Shridhar, Oscar Söderberg, and Mats Nordenborg, and became popular with remixes of old Bollywood hits such as "Kya Soorat Hai", "Woh Chali" and "Chod Do Anchal".
