INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Nikhil-Vinay', 'Nikhil–Vinay were an Indian music director duo consisting of Nikhil Kamath and Vinay Tiwari. The duo were active in the Hindi film industry from 1991 until their split in 2006.
