INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Nikhita Gandhi', 'Nikhita Gandhi (born 1 October 1991) is an Indian playback singer who has worked in Indian films in five different languages. She has worked in Tamil, Hindi, Telugu, Bengali, and Kannada film projects.
