INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Nizami Brothers', 'Feroz Nizami (born Ferozuddin Ahmad; 10 November 1910 – 15 November 1975), was a Pakistani film score composer, music director and classical singer. 
