INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Papanasam Sivan', 'Damal Krishnaswamy Pattammal () (19 March 1919 – 16 July 2009), popularly known as D. K. Pattammal or DKP, was an Indian Carnatic musician and a playback singer for film songs in Tamil. Pattammal, along with her contemporaries M. S. Subbulakshmi and M. L. Vasanthakumari, are popularly referred to as the female trinity of Carnatic Music. This trio initiated the entry of women into mainstream Carnatic Music. She has been appreciated all over the world by Carnatic music lovers.', '1919-03-19', 0);

