INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Prasoon Joshi', 'Prasoon Joshi (born 16 September 1971) is an Indian poet, writer, lyricist, screenwriter, communication specialist and marketer. He is the CEO of McCann World group India and Chairman APAC (Asia Pacific), a subsidiary of the global marketing firm McCann Erickson. He was appointed as the Chairperson of the Central Board of Film Certification on 11 August 2017.
