INSERT OR REPLACE INTO ArtistInfo (name, biography, birth_date, monthly_listeners)
VALUES ('Pravesh Lal Yadav', 'Kirori Mal College is a constituent college of the University of Delhi. Established in 1954, it is located in the heart of North Campus of the university in New Delhi, and offers undergraduate and postgraduate programmes in the sciences, humanities and commerce. The National Assessment and Accreditation Council accredited it with a CGPA of 3.54 (A++) in 2023, which is the third highest among all Delhi University colleges.
